SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_porc_trein (ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

/* 
IE_OPCAO_P 
P - Produtivas 
A - Apoio 
T- Treinamento 
ADM - Administrativo 
*/
				 
				 
qt_total_horas_w	double precision;
qt_horas_w		double precision;
qt_retorno_w		double precision;
				

BEGIN 
 
SELECT SUM(((a.qt_minuto)/60)) qt_horas 
into STRICT 	qt_total_horas_w 
FROM  man_ordem_servico_v v, 
    man_ordem_serv_ativ a, 
    man_tipo_funcao b, 
    man_grupo_funcao c 
WHERE  v.nr_sequencia  = a.nr_seq_ordem_serv 
AND   a.nr_seq_funcao = b.nr_sequencia 
AND   b.nr_seq_grupo_funcao = c.nr_sequencia 
AND  	c.nr_sequencia IN (2,4,5,6,7,8,9,10,11,13);
 
if (ie_opcao_p = 'P') then 
	 
	SELECT SUM(((a.qt_minuto)/60)) qt_horas 
	into STRICT	qt_horas_w 
	FROM  man_ordem_servico_v v, 
		man_ordem_serv_ativ a, 
		man_tipo_funcao b, 
		man_grupo_funcao c 
	WHERE  v.nr_sequencia  = a.nr_seq_ordem_serv 
	AND   a.nr_seq_funcao = b.nr_sequencia 
	AND   b.nr_seq_grupo_funcao = c.nr_sequencia 
	AND   c.nr_sequencia IN (2,4,6,11);
	 
	qt_retorno_w	:= dividir((qt_horas_w * 100),qt_total_horas_w);
end if;
 
if (ie_opcao_p = 'A') then 
	 
	SELECT SUM(((a.qt_minuto)/60)) qt_horas 
	into STRICT	qt_horas_w 
	FROM man_ordem_servico_v v, 
	   man_ordem_serv_ativ a, 
	   man_tipo_funcao b, 
	   man_grupo_funcao c 
	WHERE v.nr_sequencia  = a.nr_seq_ordem_serv 
	AND  a.nr_seq_funcao = b.nr_sequencia 
	AND   b.nr_seq_grupo_funcao = c.nr_sequencia 
	AND  c.nr_sequencia IN (7,8,9);
	 
	qt_retorno_w	:= dividir((qt_horas_w * 100),qt_total_horas_w);
end if;
 
if (ie_opcao_p = 'T') then 
	 
	SELECT SUM(((a.qt_minuto)/60)) qt_horas 
	into STRICT	qt_horas_w 
	FROM man_ordem_servico_v v, 
	   man_ordem_serv_ativ a, 
	   man_tipo_funcao b, 
	   man_grupo_funcao c 
	WHERE v.nr_sequencia  = a.nr_seq_ordem_serv 
	AND  a.nr_seq_funcao = b.nr_sequencia 
	AND   b.nr_seq_grupo_funcao = c.nr_sequencia 
	AND  c.nr_sequencia IN (10);
	 
	qt_retorno_w	:= dividir((qt_horas_w * 100),qt_total_horas_w);
end if;
 
if (ie_opcao_p = 'ADM') then 
		 
	SELECT sum(((a.qt_minuto)/60)) qt_horas 
	into STRICT	qt_horas_w 
	FROM man_ordem_servico_v v, 
	   man_ordem_serv_ativ a, 
	   man_tipo_funcao b, 
	   man_grupo_funcao c 
	WHERE v.nr_sequencia  = a.nr_seq_ordem_serv 
	AND  a.nr_seq_funcao = b.nr_sequencia 
	AND   b.nr_seq_grupo_funcao = c.nr_sequencia 
	AND  c.nr_sequencia IN (5,13);
	 
	qt_retorno_w	:= dividir((qt_horas_w * 100),qt_total_horas_w);
end if;
 
return	qt_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
