SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_portador_titulo_data ( nr_titulo_p bigint, dt_referencia_p timestamp, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/* 1 - cd_portador
   2 - cd_tipo_portador
   3 - ds_portador
   4 - ds_tipo_portador
   5 - cd_tipo_portador / ds_tipo_portador
*/
cd_portador_w		bigint;
cd_tipo_portador_w	bigint;
nr_seq_alt_w		bigint;
ds_retorno_w		varchar(255);
dt_inicial_w		timestamp;
dt_final_w		timestamp;

c01 CURSOR FOR
SELECT 	cd_portador,
	cd_tipo_portador
from	alteracao_portador
where 	nr_titulo			= nr_titulo_p
and	dt_alteracao			<= dt_final_w
order 	by dt_alteracao,
	nr_sequencia;

c02 CURSOR FOR
SELECT 	cd_portador_ant,
	cd_tipo_portador_ant
from	alteracao_portador
where 	nr_titulo			= nr_titulo_p
and	dt_alteracao			>= dt_inicial_w
order 	by dt_alteracao desc,
	nr_sequencia desc;



BEGIN

cd_portador_w				:= null;
cd_tipo_portador_w			:= null;
dt_inicial_w				:= trunc(dt_referencia_p);
dt_final_w				:= fim_dia(dt_referencia_p);

open c01;
loop
fetch c01 into
	cd_portador_w,
	cd_tipo_portador_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

if (coalesce(cd_portador_w::text, '') = '') then
	open c02;
	loop
	fetch c02 into
		cd_portador_w,
		cd_tipo_portador_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
	end loop;
	close c02;

	if (coalesce(cd_portador_w::text, '') = '') then

		select 	max(cd_portador),
			max(cd_tipo_portador)
		into STRICT	cd_portador_w,
			cd_tipo_portador_w
		from 	titulo_receber
		where 	nr_titulo = nr_titulo_p;

	end if;
end if;

if (ie_opcao_p = 1) then

	ds_retorno_w := cd_portador_w;

elsif (ie_opcao_p = 2) then

	ds_retorno_w := cd_tipo_portador_w;

elsif (ie_opcao_p = 3) then

	ds_retorno_w	:= substr(obter_desc_portador(cd_tipo_portador_w,cd_portador_w),1,255);

elsif (ie_opcao_p = 4) then

	ds_retorno_w := substr(obter_valor_dominio(703, cd_tipo_portador_w),1,255);

elsif (ie_opcao_p = 5) then

	ds_retorno_w := cd_tipo_portador_w || ' - ' || substr(obter_desc_portador(cd_tipo_portador_w,cd_portador_w),1,255);
end if;

return 	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
