CREATE OR REPLACE FUNCTION obter_posicao_ciclo_autor (nr_seq_paciente_setor_p bigint, nr_seq_paciente_p bigint, nr_ciclo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);
nr_maior_ciclo_w	integer;


BEGIN

if (nr_seq_paciente_setor_p IS NOT NULL AND nr_seq_paciente_setor_p::text <> '') then
	begin

	begin
	select	max(x.nr_ciclo)
	into STRICT	nr_maior_ciclo_w
	from	autorizacao_convenio x
	where	x.nr_seq_paciente_setor = nr_seq_paciente_setor_p;
	exception
	when others then
		nr_maior_ciclo_w := null;
	end;



	ds_retorno_w := nr_ciclo_p ||'/'|| nr_maior_ciclo_w;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
