CREATE OR REPLACE FUNCTION obter_posicao_cirurgia_agenda ( nr_cirurgia_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_posicao_w	varchar(80);


BEGIN

select	max(substr(coalesce(obter_valor_dominio(1552,a.ie_posicao), obter_valor_dominio(8664,a.ie_posicao)),1,80))
into STRICT	ds_posicao_w
from	posicao_cirurgia a,
		agenda_paciente b
where	a.nr_cirurgia = b.nr_cirurgia
and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
and		coalesce(a.dt_inativacao::text, '') = ''
and		a.nr_cirurgia = nr_cirurgia_p;

return ds_posicao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_posicao_cirurgia_agenda ( nr_cirurgia_p bigint ) FROM PUBLIC;

