CREATE OR REPLACE FUNCTION obter_posicao_grupo ( cd_estabelecimento_p bigint, cd_perfil_p bigint, cd_setor_usuario_p bigint, nm_usuario_p text, ie_grupo_p text, ie_funcao_p text ) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w			cpoe_regra_ordenacao.nr_sequencia%type;
nr_seq_apresentacao_w	cpoe_regra_ordem_grupo.nr_seq_apresentacao%type;

c01 CURSOR FOR
SELECT	a.nr_seq_regra_ordem
from	cpoe_regra_ordem_lib_v a
where	coalesce(a.cd_estabelecimento, coalesce(cd_estabelecimento_p,0)) = coalesce(cd_estabelecimento_p,0)
and		coalesce(a.cd_setor_atendimento, coalesce(cd_setor_usuario_p,0)) = coalesce(cd_setor_usuario_p,0)
and		coalesce(a.nm_usuario_regra, coalesce(nm_usuario_p,'XPTO')) = coalesce(nm_usuario_p,'XPTO')
and		coalesce(a.cd_perfil, coalesce(cd_perfil_p,0)) = coalesce(cd_perfil_p,0)
and		coalesce(a.ie_situacao,'A') = 'A'
--and 		nvl(a.ie_funcao_cpoe, 'A') = ie_funcao_p
and (a.ie_funcao_cpoe = 'A' or a.ie_funcao_cpoe = ie_funcao_p)
order by	coalesce(a.nm_usuario_regra,'') desc,
			coalesce(a.cd_setor_atendimento,0),
			coalesce(a.cd_perfil,0),
			coalesce(a.cd_estabelecimento,0);

BEGIN

open c01;
loop
fetch c01 into	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

select	max(nr_seq_apresentacao)
into STRICT	nr_seq_apresentacao_w
from	cpoe_regra_ordem_grupo
where	nr_seq_regra_ordem = nr_sequencia_w
and		ie_grupo = ie_grupo_p;

return nr_seq_apresentacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
