CREATE OR REPLACE FUNCTION obter_posicao_inicial ( nr_cirurgia_p bigint ) RETURNS bigint AS $body$
DECLARE

    ie_posicao_w bigint;

BEGIN
    SELECT
        MAX(a_aux.ie_posicao)
    INTO STRICT ie_posicao_w
    FROM
        agenda_paciente            a,
        agenda_paciente_auxiliar   a_aux,
        posicao_cirurgia           pos_cir
    WHERE
        pos_cir.nr_cirurgia = a.nr_cirurgia
        AND a.nr_sequencia = a_aux.nr_seq_agenda
        AND a.nr_cirurgia = nr_cirurgia_p;

    RETURN ie_posicao_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
