CREATE OR REPLACE FUNCTION obter_posicao_item_nota_ipe ( nr_seq_item_p bigint, nr_interno_conta_p bigint, nr_seq_protocolo_p bigint, cd_item_convenio_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

/*
IE_OPCAO_P:
F-Folha
L-Linha
N-Nota
*/
nr_retorno_w		bigint := 0;


BEGIN

if (ie_opcao_p = 'F') then
	begin

	begin
	select	coalesce(max(nr_folha_item),0)
	into STRICT	nr_retorno_w
	from	w_posicao_item_nota_ipe
	where	nr_seq_item	= nr_seq_item_p
	and	nr_interno_conta	= nr_interno_conta_p
	and	cd_item_convenio	= cd_item_convenio_p
	and	nr_seq_protocolo	= nr_seq_protocolo_p;
	exception
	when others then
		nr_retorno_w := 0;
	end;

	end;
elsif (ie_opcao_p = 'L') then
	begin

	begin
	select	coalesce(max(nr_linha_item),0)
	into STRICT	nr_retorno_w
	from	w_posicao_item_nota_ipe
	where	nr_seq_item	= nr_seq_item_p
	and	nr_interno_conta	= nr_interno_conta_p
	and	cd_item_convenio	= cd_item_convenio_p
	and	nr_seq_protocolo	= nr_seq_protocolo_p;
	exception
	when others then
		nr_retorno_w := 0;
	end;

	end;
elsif (ie_opcao_p = 'N') then
	begin

	begin
	select	coalesce(max(nr_nota_item),0)
	into STRICT	nr_retorno_w
	from	w_posicao_item_nota_ipe
	where	nr_seq_item	= nr_seq_item_p
	and	nr_interno_conta	= nr_interno_conta_p
	and	cd_item_convenio	= cd_item_convenio_p
	and	nr_seq_protocolo	= nr_seq_protocolo_p;
	exception
	when others then
		nr_retorno_w := 0;
	end;

	end;
end if;

return nr_retorno_w;

end;
$body$
