CREATE OR REPLACE FUNCTION obter_possui_diag_medic (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1)	:= 'N';
qt_diag_w	integer;


BEGIN

select	count(*)
into STRICT	qt_diag_w
from	checkup_medic_associado	c,
	checkup_tipo_diag	b,
	checkup_diagnostico	a
where	a.nr_atendimento	= nr_atendimento_p
and	a.nr_seq_diag		= b.nr_sequencia
and	b.nr_sequencia    	= c.nr_seq_diag
and	a.ie_situacao		= 'A';

if (qt_diag_w > 0) then
	ds_retorno_w	:= 'S';
end if;

RETURN ds_retorno_w;

END;
