CREATE OR REPLACE FUNCTION obter_possui_reg_pendente ( nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
qt_comunic_w	bigint;
qt_rep_lib_w	bigint;
qt_rep_lib_ww	bigint;
qt_comunic_resp_w bigint;


BEGIN

ds_retorno_w	:= 'N';

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
		select	count(*)
		into STRICT	qt_comunic_w
		from	prescr_mat_comunic_cih h
		where	h.nr_prescricao		= nr_prescricao_p
		and	h.nr_seq_prescricao 	= nr_sequencia_p
		and	coalesce(nr_seq_pres_mat_com::text, '') = ''
		and	coalesce(dt_ciente::text, '') = '';

		if (qt_comunic_w > 0) then
			ds_retorno_w := 'C';
		end if;

		select	count(*)
		into STRICT	qt_comunic_resp_w
		from	prescr_mat_comunic_cih h
		where	h.nr_prescricao		= nr_prescricao_p
		and	h.nr_seq_prescricao 	= nr_sequencia_p
		and	(nr_seq_pres_mat_com IS NOT NULL AND nr_seq_pres_mat_com::text <> '');

		if (qt_comunic_resp_w > 0) then
			ds_retorno_w := 'R';
		end if;

		select	count(*)
		into STRICT	qt_rep_lib_ww
		from	prescr_material
		where	nr_sequencia = nr_sequencia_p
		and	nr_prescricao =  nr_prescricao_p
		and	((coalesce(qt_dias_liberado::text, '') = '') or (coalesce(qt_dias_solicitado,0) = 0));

		select	count(*)
		into STRICT	qt_rep_lib_w
		
		where	(obter_usuario_reproc_cih(nr_prescricao_p, nr_sequencia_p) IS NOT NULL AND (obter_usuario_reproc_cih(nr_prescricao_p, nr_sequencia_p))::text <> '');


		if (qt_rep_lib_ww > 0) and (qt_rep_lib_w = 0) then
			ds_retorno_w := 'L';
		end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
