CREATE OR REPLACE FUNCTION obter_potencia_convertida (ds_valor_p text) RETURNS varchar AS $body$
DECLARE


vl_base_w		varchar(1000);
vl_expoente_w		varchar(1000);
ie_formula_ok_w		integer;
ds_formula_aux_w	varchar(2000);
ie_tamanho_w		integer;
i			integer;


BEGIN

ie_formula_ok_w		:= position('^' in ds_valor_p);
ds_formula_aux_w 	:= ds_valor_p;

if (ie_formula_ok_w > 0) then

	while(position('^' in ds_formula_aux_w) > 0) loop

		ie_formula_ok_w := position('^' in ds_formula_aux_w);
		ie_tamanho_w := 0;

		for i in 1..length(ds_formula_aux_w) + 1 loop
			if	((substr(ds_formula_aux_w, i, 1) in (' ','/','*','+','-','(',')','@',',')) or (i = length(ds_formula_aux_w) + 1)) and
				(i < (ie_formula_ok_w)) then

				ie_tamanho_w := i;
			end if;
		end loop;

		vl_base_w	:= substr(ds_formula_aux_w, ie_tamanho_w + 1, ie_formula_ok_w - (ie_tamanho_w + 1));

		for i in ie_formula_ok_w..length(ds_formula_aux_w) + 1 loop
			if	((substr(ds_formula_aux_w, i, 1) in (' ','/','*','+','-','(',')','@',',')) or (i = length(ds_formula_aux_w) + 1)) and
				(i > (ie_formula_ok_w + 1)) then

				ie_tamanho_w := i;
				exit;
			end if;
		end loop;

		vl_expoente_w	:= substr(ds_formula_aux_w, ie_formula_ok_w + 1, ie_tamanho_w - (ie_formula_ok_w + 1));

		ds_formula_aux_w	:= replace((replace(ds_formula_aux_w, vl_base_w||'^'||vl_expoente_w, 'power('||vl_base_w||','||vl_expoente_w||')')),' ','');

	end loop;
end if;

return	ds_formula_aux_w;

end;
$body$
