CREATE OR REPLACE FUNCTION obter_precaucao_isolamento_pf (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
nr_seq_precaucao_atend_w	atendimento_precaucao.nr_sequencia%type;
nr_seq_precaucao_w		atendimento_precaucao.nr_seq_precaucao%type;

C01 CURSOR FOR
	SELECT	at.nr_sequencia
	from	atendimento_precaucao at
			inner join cih_precaucao cih_p on cih_p.nr_sequencia = at.nr_seq_precaucao
	where	at.cd_pessoa_fisica = cd_pessoa_fisica_p
	and		coalesce(at.dt_inativacao::text, '') = ''
	and		((coalesce(at.dt_termino::text, '') = '') or (clock_timestamp() between coalesce(at.dt_inicio, clock_timestamp() - interval '1 days') and at.dt_termino))
	and		(at.dt_liberacao IS NOT NULL AND at.dt_liberacao::text <> '')
	and		coalesce(at.dt_final_precaucao::text, '') = ''
	order by coalesce(cih_p.nr_seq_prioridade,0), at.nr_sequencia;


BEGIN

nr_seq_precaucao_atend_w	:= 0;

open C01;
loop
fetch C01 into	
	nr_seq_precaucao_atend_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

if (nr_seq_precaucao_atend_w > 0) then

	select  max(nr_seq_precaucao)
	into STRICT	nr_seq_precaucao_w
	from	atendimento_precaucao
	where	nr_sequencia = nr_seq_precaucao_atend_w;

	select  max(ds_precaucao)
	into STRICT	ds_retorno_w
	from	cih_precaucao
	where	nr_sequencia = nr_seq_precaucao_w;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
