SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_precaucao_isolamento ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
ds_auxiliar_w 			varchar(200);
nr_seq_precaucao_atend_w	bigint;
nr_seq_precaucao_w		bigint;
nr_Seq_motivo_precaucao_w	bigint;

/*ie_opcao_p
C   -  Código
MO  -  Motivo
MOC -  Motivo Concatenado
CM  -  Código do motivo
DI  -  Data Inicio
DF  -  Data Fim
QD  -  Dias de isolamento
COR -  Cor do Isolamento
SEQ -  Sequência da atendimento precaucao
*/
C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	atendimento_precaucao a ,
			cih_precaucao b
	where	a.nr_atendimento = nr_atendimento_p
	and		b.nr_sequencia	= a.nr_seq_precaucao
	and		coalesce(a.dt_inativacao::text, '') = ''
	and		((coalesce(a.dt_termino::text, '') = '') or (clock_timestamp() between coalesce(a.DT_INICIO,clock_timestamp() - interval '1 days') and a.DT_TERMINO))
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_final_precaucao::text, '') = ''
	order by coalesce(b.nr_seq_prioridade,0),a.nr_sequencia;

BEGIN
/*
select	nvl(max(nr_Sequencia),0)
into	nr_seq_precaucao_atend_w
from	atendimento_precaucao a
where	nr_atendimento = nr_atendimento_p
and	dt_inativacao is null
and	((dt_termino is null) or (sysdate between nvl(a.DT_INICIO,sysdate-1) and a.DT_TERMINO))
and	dt_liberacao is not null;
*/
nr_seq_precaucao_atend_w	:= 0;

open C01;
loop
fetch C01 into
	nr_seq_precaucao_atend_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

if (nr_seq_precaucao_atend_w > 0) then

	select  max(nr_seq_precaucao),
		max(nr_seq_motivo_isol)
	into STRICT	nr_seq_precaucao_w,
		nr_Seq_motivo_precaucao_w
	from	atendimento_precaucao
	where	nr_sequencia = nr_seq_precaucao_atend_w;

	select  max(ds_precaucao)
	into STRICT	ds_retorno_w
	from	cih_precaucao
	where	nr_sequencia = nr_seq_precaucao_w;

	if (ie_opcao_p = 'MO') then
		select	substr(obter_desc_motivo_isolamento(nr_Seq_motivo_precaucao_w), 1, 100)
		into STRICT	ds_retorno_w
		;
	elsif (ie_opcao_p = 'MOC') then
		ds_retorno_w := null;
		open C01;
		loop
		fetch C01 into
			nr_seq_precaucao_atend_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */

			if (nr_seq_precaucao_atend_w IS NOT NULL AND nr_seq_precaucao_atend_w::text <> '') then
				select  max(nr_seq_precaucao),
						max(nr_seq_motivo_isol)
				into STRICT	nr_seq_precaucao_w,
						nr_Seq_motivo_precaucao_w
				from	atendimento_precaucao
				where	nr_sequencia = nr_seq_precaucao_atend_w;

				if (nr_Seq_motivo_precaucao_w IS NOT NULL AND nr_Seq_motivo_precaucao_w::text <> '') then
					select obter_desc_motivo_isolamento(nr_Seq_motivo_precaucao_w)
					into STRICT ds_auxiliar_w
					;

					if (ds_auxiliar_w IS NOT NULL AND ds_auxiliar_w::text <> '') then
						if (coalesce(ds_retorno_w::text, '') = '') then
							ds_retorno_w := substr(ds_auxiliar_w,1,255);
						else
							ds_retorno_w := substr(ds_retorno_w || ',' || ds_auxiliar_w,1,255);
						end if;
					end if;
				end if;
			end if;

		end loop;
		close C01;


	elsif (ie_opcao_p = 'DI') then
		select  max(to_char(dt_inicio,'dd/mm/yyyy hh24:mi:ss'))
		into STRICT	ds_retorno_w
		from	atendimento_precaucao
		where	nr_sequencia = nr_seq_precaucao_atend_w;
	elsif (ie_opcao_p = 'DF') then
		select  max(to_char(dt_termino,'dd/mm/yyyy hh24:mi:ss'))
		into STRICT	ds_retorno_w
		from	atendimento_precaucao
		where	nr_sequencia = nr_seq_precaucao_atend_w
		and	(dt_termino IS NOT NULL AND dt_termino::text <> '');
	elsif (ie_opcao_p = 'QD') then
		select  max(coalesce(obter_dias_entre_datas(dt_inicio,coalesce(dt_termino,clock_timestamp())),0))
		into STRICT	ds_retorno_w
		from	atendimento_precaucao
		where	nr_sequencia = nr_seq_precaucao_atend_w;
     elsif (ie_opcao_p = 'COR') then
       		select  max(ds_cor)
		into STRICT	ds_retorno_w
		from	cih_precaucao
		where	nr_sequencia = nr_seq_precaucao_w;
	end if;

end if;

if (upper(ie_opcao_p) = 'C') then
        return nr_seq_precaucao_w;
elsif (upper(ie_opcao_p) = 'CM') then
	return nr_Seq_motivo_precaucao_w;
elsif (upper(ie_opcao_p) = 'SEQ') then
	return nr_seq_precaucao_atend_w;
else
	return ds_retorno_w;             /*ie_opcao_p  'MO' 'DI' 'DF' 'QD' 'COR'*/
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_precaucao_isolamento ( nr_atendimento_p bigint, ie_opcao_p text) FROM PUBLIC;

