CREATE OR REPLACE FUNCTION obter_precaucao_isol_concat ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(100);
ds_retorno_concat_w		varchar(4000);
nr_seq_precaucao_atend_w	bigint;
nr_seq_precaucao_w		bigint;
nr_Seq_motivo_precaucao_w	bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	atendimento_precaucao a ,
			cih_precaucao b
	where	a.nr_atendimento = nr_atendimento_p
	and		b.nr_sequencia	= a.nr_seq_precaucao
	and		coalesce(a.dt_inativacao::text, '') = ''
	and		((coalesce(a.dt_termino::text, '') = '') or (clock_timestamp() between coalesce(a.DT_INICIO,clock_timestamp() - interval '1 days') and a.DT_TERMINO))
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_final_precaucao::text, '') = ''
	order by coalesce(b.nr_seq_prioridade,0),a.nr_sequencia;

BEGIN

nr_seq_precaucao_atend_w	:= 0;

open C01;
loop
fetch C01 into
	nr_seq_precaucao_atend_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if ( nr_seq_precaucao_atend_w > 0) then

		select  max(nr_seq_precaucao),
			max(nr_seq_motivo_isol)
		into STRICT	nr_seq_precaucao_w,
			nr_Seq_motivo_precaucao_w
		from	atendimento_precaucao
		where	nr_sequencia = nr_seq_precaucao_atend_w;

		select  max(ds_precaucao)
		into STRICT	ds_retorno_w
		from	cih_precaucao
		where	nr_sequencia = nr_seq_precaucao_w;

		if (ds_retorno_concat_w IS NOT NULL AND ds_retorno_concat_w::text <> '') then
			ds_retorno_concat_w := substr(ds_retorno_concat_w || ', ',1,4000);
		end if;
		ds_retorno_concat_w := substr(ds_retorno_concat_w || ds_retorno_w,1,4000);

	end if;
	end;
end loop;
close C01;


return ds_retorno_concat_w;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
