CREATE OR REPLACE FUNCTION obter_precaucoes_isolamento ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_motivo_isol_w bigint;
nr_seq_precaucao_w  bigint;
ds_retorno_w   varchar(4000);

C01 CURSOR FOR
	 SELECT a.nr_sequencia
	 FROM 	atendimento_precaucao a ,
			cih_precaucao b
	 WHERE 	a.nr_atendimento = nr_atendimento_p
	 AND  	b.nr_sequencia = a.nr_seq_precaucao
	 AND  	coalesce(a.dt_inativacao::text, '') = ''
	 AND  	((coalesce(a.dt_termino::text, '') = '') OR (clock_timestamp() BETWEEN coalesce(a.DT_INICIO,clock_timestamp() - interval '1 days') AND a.DT_TERMINO))
	 AND  	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	 ORDER 	BY coalesce(b.nr_seq_prioridade,0),a.nr_sequencia;

C02 CURSOR FOR
	SELECT 	nr_seq_motivo_isol
	FROM 	atend_precaucao_motivo
	WHERE 	nr_seq_atend_precaucao = nr_seq_precaucao_w
	ORDER 	BY 1;


BEGIN

nr_seq_precaucao_w := 0;

OPEN C01;
LOOP
FETCH C01 INTO
	nr_seq_precaucao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
END LOOP;
CLOSE C01;

IF (coalesce(nr_seq_precaucao_w,0) > 0) THEN

  OPEN C02;
  LOOP
  FETCH C02 INTO
		nr_seq_motivo_isol_w;
  EXIT WHEN NOT FOUND; /* apply on C02 */
   BEGIN
   IF (coalesce(nr_seq_motivo_isol_w,0) > 0) THEN
		IF (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') THEN
			ds_retorno_w := ds_retorno_w||', '||SUBSTR(obter_desc_motivo_isolamento(nr_seq_motivo_isol_w),1,150);
		ELSE
			ds_retorno_w := SUBSTR(obter_desc_motivo_isolamento(nr_seq_motivo_isol_w),1,150);
		END IF;
   END IF;
   END;
  END LOOP;
  CLOSE C02;
END IF;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
