CREATE OR REPLACE FUNCTION obter_preco_custo_material ( cd_material_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
vl_preco_custo_w	double precision := 0;
cd_material_w		material.cd_material%type;


BEGIN 
 
vl_preco_custo_w	:= coalesce(obter_valor_ultima_compra(cd_estabelecimento_p,730,cd_material_p,null,'N'),0);
 
if (vl_preco_custo_w = 0) then 
 
	Select	coalesce(cd_material_generico,cd_material) 
	into STRICT	cd_material_w 
	from	material 
	where	cd_material = cd_material_p;
 
	vl_preco_custo_w	:= coalesce(obter_valor_ultima_compra(cd_estabelecimento_p,730,cd_material_w,null,'N'),0);
end if;	
 
return	vl_preco_custo_w;
 
end;
$body$
