CREATE OR REPLACE FUNCTION obter_preco_mat_conv (cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, cd_material_p bigint, dt_vigencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_preco_w	double precision := 0;
dt_vigencia_w	timestamp;

c01 CURSOR FOR
SELECT	/*+index (a prcmate_pk) */	coalesce(a.vl_preco_venda,0),
	a.dt_inicio_vigencia
from	preco_material a,
	convenio_preco_mat b
where	a.cd_tab_preco_mat	= b.cd_tab_preco_mat
and 	a.cd_estabelecimento    = b.cd_estabelecimento
and	b.cd_convenio		= cd_convenio_p
and	b.cd_categoria		= cd_categoria_p
and	a.cd_estabelecimento 	= cd_estabelecimento_p
and	a.cd_material        	= cd_material_p
and	coalesce(a.ie_situacao,'A')	= 'A'
and	coalesce(b.ie_situacao,'A')	= 'A'
and	pkg_date_utils.start_of(dt_vigencia_p,'DD') between a.dt_inicio_vigencia and pkg_date_utils.end_of(coalesce(a.dt_final_vigencia,dt_vigencia_p), 'DAY')
order by
	coalesce(b.nr_prioridade,1) desc,
	coalesce(a.cd_cgc_fornecedor,'0'),
	a.dt_inicio_vigencia,
	a.vl_preco_venda;


BEGIN

open	c01;
loop
fetch	c01 into
	vl_preco_w,
	dt_vigencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	vl_preco_w:= vl_preco_w;

	end;
end loop;
close c01;

return	vl_preco_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
