CREATE OR REPLACE FUNCTION obter_preco_mat_tabela ( cd_tab_preco_mat_p bigint, cd_material_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


ie_retorno_w		double precision;


BEGIN

SELECT  DISTINCT
	MAX(coalesce(b.vl_preco_venda,0))
into STRICT	ie_retorno_w
FROM 	tabela_preco_material a,
	preco_material b
WHERE	a.cd_tab_preco_mat 		= b.cd_tab_preco_mat
AND	a.cd_estabelecimento  		= b.cd_estabelecimento
AND	a.cd_tab_preco_mat  	 	= cd_tab_preco_mat_p
and	a.cd_estabelecimento 		= cd_estabelecimento_p
AND	b.cd_material 			= cd_material_p
AND	b.dt_inicio_vigencia BETWEEN dt_inicial_p AND dt_final_p;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
