CREATE OR REPLACE FUNCTION obter_preco_mat_tab_compras ( cd_estabelecimento_p bigint, cd_material_p bigint, cd_tabela_p bigint, cd_unidade_medida_param_p text) RETURNS bigint AS $body$
DECLARE


vl_material_w			double precision;
cd_unidade_medida_compra_w	varchar(30);
cd_unidade_medida_estoque_w	varchar(30);
cd_unidade_medida_consumo_w	varchar(30);
qt_conv_compra_estoque_w		double precision;
qt_conv_estoque_consumo_w	double precision;


BEGIN

select	coalesce(obter_preco_mat_tab(cd_estabelecimento_p, cd_material_p, cd_tabela_p),0)
into STRICT	vl_material_w
;

if (vl_material_w > 0) then
	select	cd_unidade_medida_compra,
		cd_unidade_medida_estoque,
		cd_unidade_medida_consumo,
		qt_conv_compra_estoque,
		qt_conv_estoque_consumo
	into STRICT	cd_unidade_medida_compra_w,
		cd_unidade_medida_estoque_w,
		cd_unidade_medida_consumo_w,
		qt_conv_compra_estoque_w,
		qt_conv_estoque_consumo_w
	from	material
	where	cd_material = cd_material_p;

	if (cd_unidade_medida_param_p = cd_unidade_medida_compra_w) then
		vl_material_w	:= vl_material_w * qt_conv_compra_estoque_w * qt_conv_estoque_consumo_w;
	end if;

	if (cd_unidade_medida_param_p = cd_unidade_medida_estoque_w) then
		vl_material_w	:= vl_material_w * qt_conv_estoque_consumo_w;
	end if;
end if;

return	vl_material_w;

end;
$body$
