SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_preco_pad_prev (cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


cd_material_w		bigint;	
vl_preco_venda_w	double precision;
qt_material_w		bigint := 0;
vl_preco_venda_total_w	double precision := 0;	
vl_preco_media_w	double precision;
			
C01 CURSOR FOR
	SELECT distinct	cd_material
	from    material
	where   cd_material_estoque = cd_material_p
	and	ie_situacao = 'A';	

BEGIN
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	open C01;
	loop
	fetch C01 into	
		cd_material_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		select	max(vl_preco_venda)
		into STRICT	vl_preco_venda_w
		from	preco_material a
		where	cd_material = cd_material_w
		and	cd_tab_preco_mat = 1
		and	ie_situacao = 'A'
		and	(vl_preco_venda IS NOT NULL AND vl_preco_venda::text <> '')
		and	a.cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento
		and	dt_inicio_vigencia = (SELECT	max(b.dt_inicio_vigencia)
						from	preco_material b
						where	b.cd_material = cd_material_w
						and	b.cd_tab_preco_mat = 1
						and	b.ie_situacao = 'A'
						and	(b.vl_preco_venda IS NOT NULL AND b.vl_preco_venda::text <> '')
						and	b.dt_inicio_vigencia <= clock_timestamp()
						and	b.cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento
						and	((coalesce(b.dt_final_vigencia::text, '') = '') or (b.dt_final_vigencia >= clock_timestamp())));
		
		if (vl_preco_venda_w IS NOT NULL AND vl_preco_venda_w::text <> '') then
			qt_material_w := qt_material_w + 1;
			vl_preco_venda_total_w	:= (Campo_Mascara_virgula_casas((vl_preco_venda_total_w + vl_preco_venda_w),4))::numeric;
		end if;					
		end;
	end loop;
	close C01;
	
	if (qt_material_w > 0) then
		vl_preco_media_w := vl_preco_venda_total_w / qt_material_w;
	end if;
	
end if;

return	vl_preco_media_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
