CREATE OR REPLACE FUNCTION obter_preco_perm_sus_aih (cd_estabelecimento_p bigint, ie_preco_permanencia_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS bigint AS $body$
DECLARE


/* 0 - Preco
   1 - Permanencia
   2 - SADT + MATMED*/
vl_procedimento_w          double precision := 0;
vl_sadtmed_w               double precision := 0;
qt_permanencia_w           smallint := 0;
ie_versao_aih_w            varchar(20);
vl_retorno_w               double precision;

BEGIN

select     coalesce(max(ie_versao_aih),'0')
into STRICT       ie_versao_aih_w
from       sus_parametros
where      cd_estabelecimento      = cd_estabelecimento_p;

if (ie_versao_aih_w        <> '0') then
   begin
   select (vl_sadt + vl_matmed + vl_diaria + vl_taxas + vl_medico) vl_proc,
           (vl_sadt + vl_matmed) vl_sadtmed,
           qt_permanencia
   into STRICT    vl_procedimento_w,
           vl_sadtmed_w,
           qt_permanencia_w
   from    sus_preco_procaih
   where   ie_versao        = ie_versao_aih_w
   and     cd_procedimento  = cd_procedimento_p
   and     ie_origem_proced = ie_origem_proced_p;
   if (ie_preco_permanencia_p = 0) then
           vl_retorno_w    := vl_procedimento_w;
   elsif (ie_preco_permanencia_p = 1) then
           vl_retorno_w    := qt_permanencia_w;
   elsif (ie_preco_permanencia_p = 2) then
           vl_retorno_w    := vl_sadtmed_w;
   end if;
   end;
end if;

return     vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
