CREATE OR REPLACE FUNCTION obter_preco_proc_mat_orig (nr_sequencia_p bigint, ie_proc_mat_p text) RETURNS bigint AS $body$
DECLARE

 
vl_preco_proc_mat_w	double precision;


BEGIN 
 
if (ie_proc_mat_p = '1') then 
	 
	select 	Obter_preco_procedimento(b.cd_estabelecimento, 
					b.cd_convenio_parametro, 
					b.cd_categoria_parametro, 
					a.dt_procedimento, 
					a.cd_procedimento, 
					a.ie_origem_proced, 
					0, 0, 
					a.cd_setor_atendimento, 
					null, null, null, null, 
					0, 0, 'P') 
	into STRICT	vl_preco_proc_mat_w 
	from 	procedimento_paciente	a, 
		conta_paciente		b 
	where 	a.nr_interno_conta = b.nr_interno_conta 
	and 	a.nr_sequencia = nr_sequencia_p;
 
elsif (ie_proc_mat_p = '2') then 
	 
	select 	Obter_preco_material(b.cd_estabelecimento, 
				 	b.cd_convenio_parametro, 
					b.cd_categoria_parametro, 
					a.dt_atendimento, 
					a.cd_material, 
					0, 0, 
					a.cd_setor_atendimento, 
					null, 0, 0) 
	into STRICT	vl_preco_proc_mat_w 
	from 	material_atend_paciente	a, 
		conta_paciente		b 
	where 	a.nr_interno_conta = b.nr_interno_conta 
	and 	a.nr_sequencia = nr_sequencia_p;
 
 
end if;
 
return vl_preco_proc_mat_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
