SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_preco_simpro_mat_cot ( cd_material_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_preco_fabrica_w		double precision;
vl_preco_venda_w			double precision;
vl_preco_w			double precision;
ie_tipo_preco_w			varchar(3);
nr_seq_simpro_w			bigint;
cd_simpro_w			bigint;
qt_conversao_w			double precision;
vl_retorno_w			double precision;
vl_preco_fabrica_fracao_w		double precision;

cd_estab_simpro_w	estabelecimento.cd_estabelecimento%type;


BEGIN

cd_estab_simpro_w := wheb_usuario_pck.get_cd_estabelecimento;

select	max(nr_sequencia)
into STRICT	nr_seq_simpro_w
from	material_simpro
where	cd_material = cd_material_p
and	coalesce(dt_vigencia, clock_timestamp()) <= clock_timestamp();

select	coalesce(cd_simpro,0),
	coalesce(qt_conversao,0)
into STRICT	cd_simpro_w,
	qt_conversao_w
from	material_simpro
where	nr_sequencia = nr_seq_simpro_w;

if (cd_simpro_w > 0) then
	begin

	select	coalesce(max(vl_preco_venda),0),
		coalesce(max(vl_preco_fabrica),0),
		coalesce(max(vl_preco_fabrica_fracao),0),
		coalesce(max(ie_tipo_preco),'X')
	into STRICT	vl_preco_venda_w,
		vl_preco_fabrica_w,
		vl_preco_fabrica_fracao_w,
		ie_tipo_preco_w
	from	simpro_preco
	where	cd_simpro = cd_simpro_w
	and     coalesce(cd_estabelecimento, coalesce(cd_estab_simpro_w, 0)) = coalesce(cd_estab_simpro_w, 0)
	and	dt_vigencia = (
		SELECT	max(dt_vigencia)
		from	simpro_preco
		where	cd_simpro = cd_simpro_w
		and     coalesce(cd_estabelecimento, coalesce(cd_estab_simpro_w, 0)) = coalesce(cd_estab_simpro_w, 0)
		and	dt_vigencia <= clock_timestamp());

	if (ie_tipo_preco_w = 'V') then
		vl_preco_w	:= vl_preco_venda_w;
	elsif (ie_tipo_preco_w = 'F') then
		vl_preco_w	:= vl_preco_fabrica_w;
	end if;

	if (ie_opcao_p = 'VU') then
		vl_retorno_w	:= dividir(vl_preco_w,qt_conversao_w);
	elsif (ie_opcao_p = 'PFF') then
		vl_retorno_w	:= vl_preco_fabrica_fracao_w;
	elsif (ie_opcao_p = 'CON') then
		vl_retorno_w	:= qt_conversao_w;
	end if;

	end;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
