CREATE OR REPLACE FUNCTION obter_prescricao_lote_producao (nr_lote_producao_p bigint) RETURNS bigint AS $body$
DECLARE


nr_prescricao_w		bigint;


BEGIN

if (nr_lote_producao_p IS NOT NULL AND nr_lote_producao_p::text <> '') then
	SELECT  max(a.nr_prescricao)
	INTO STRICT	nr_prescricao_w
	FROM	lote_producao a
	WHERE   a.nr_lote_producao = nr_lote_producao_p;

end if;

return	nr_prescricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
