SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_prescricao_nao_liberada ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE

			 
nr_retorno_w		bigint;


BEGIN 
 
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	 
	nr_retorno_w := 0;
		 
	if (nr_atendimento_p > 0) then 
		begin 
		nr_retorno_w	:= nr_atendimento_p;
		end;
	else	 
		begin 
		 
		select	coalesce(max(nr_atendimento),0) 
		into STRICT	nr_retorno_w 
		from	atendimento_paciente 
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
		and	coalesce(obter_setor_atendimento(nr_atendimento),0) > 0 
		and 	coalesce(dt_alta::text, '') = '';
		end;
		 
		if (nr_retorno_w > 0) then 
			begin 
			 
			select	coalesce(max(nr_prescricao),0) 
			into STRICT	nr_retorno_w 
			from	prescr_medica 
			where	nm_usuario_original	= nm_usuario_p 
			and	coalesce(dt_liberacao::text, '') = '' 
			and	coalesce(dt_liberacao_medico::text, '') = '' 
			and	dt_validade_prescr > clock_timestamp() 
			and	ie_adep = 'S' 
			and	nr_atendimento = nr_retorno_w;
		end;
	end if;
	end if;	
	end;
end if;
 
return	nr_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
