SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_prescricao_nr_seq_hor ( ie_tipo_item_p text, nr_seq_horario_p bigint, nr_prescricoes_p text) RETURNS bigint AS $body$
DECLARE


nr_prescricao_w		bigint := 0;


BEGIN

if (coalesce(nr_seq_horario_p,0) > 0) then
	if (ie_tipo_item_p in ('G', 'P', 'I', 'C')) then
		select 	max(nr_prescricao)
		into STRICT	nr_prescricao_w
		from 	prescr_proc_hor
		where 	nr_sequencia = nr_seq_horario_p;
	elsif (ie_tipo_item_p = 'O') then
		select 	max(nr_prescricao)
		into STRICT	nr_prescricao_w
		from 	prescr_gasoterapia_hor
		where 	nr_sequencia = nr_seq_horario_p;
	elsif (ie_tipo_item_p not in ('E', 'R', 'D')) then
		select 	max(nr_prescricao)
		into STRICT	nr_prescricao_w
		from 	prescr_mat_hor
		where 	nr_sequencia = nr_seq_horario_p;
	elsif (ie_tipo_item_p = 'R') then
		select 	max(nr_prescricao)
		into STRICT	nr_prescricao_w
		from 	prescr_rec_hor
		where 	nr_sequencia = nr_seq_horario_p;
	elsif (ie_tipo_item_p	= 'E') then
		select 	max(a.nr_prescricao)
		into STRICT	nr_prescricao_w
		from 	pe_prescricao a,
				pe_prescr_proc b,
				pe_prescr_proc_hor c
		where 	b.nr_seq_prescr = a.nr_sequencia
		and 	b.nr_sequencia = c.nr_seq_pe_proc
		and 	c.nr_sequencia = nr_seq_horario_p;
	end if;
elsif (ie_tipo_item_p = 'D') or (coalesce(nr_seq_horario_p,0) = 0) then
	select	Obter_Max_NrPrescricao(nr_prescricoes_p)
	into STRICT	nr_prescricao_w
	;
end if;

return	nr_prescricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
