CREATE OR REPLACE FUNCTION obter_prescricao ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, ie_prescr_nao_liberada_p text, nr_prescricao_p bigint, nr_prescricoes_p text, nr_prescr_titulo_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE

			 
