	begin 
	 
	if (ie_prescr_nao_liberada_p = 'S') then 
		begin 
		 
		nr_prescricao_w	:= obter_prescricao_nao_liberada(nr_atendimento_p,cd_pessoa_fisica_p,nm_usuario_p);
		end;	
	else	if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then 
			begin 
			 
			nr_prescricao_w	:= nr_prescricao_p;
			end;
	else	if (nr_prescricoes_p IS NOT NULL AND nr_prescricoes_p::text <> '') and (nr_prescricoes_p <> '') then 
			begin 
			 
			nr_prescricao_w	:=	plt_obter_max_nr_prescricao(nr_prescricoes_p);
			end;
	else	if (nr_prescr_titulo_p IS NOT NULL AND nr_prescr_titulo_p::text <> '') and (nr_prescr_titulo_p <> '') then 
			begin 
			 
			nr_prescricao_w	:=	(nr_prescr_titulo_p)::numeric;
			end;
	    end if;
	  end if;
	 end if;
	end if;
