SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_prescricoes_conta ( nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000);
nr_prescricao_w	bigint;
nr_contador_w	bigint;

C01 CURSOR FOR
	SELECT	nr_prescricao
	from (
		SELECT	a.nr_prescricao
		from	procedimento_paciente a
		where	a.nr_interno_conta = nr_interno_conta_p
		and	coalesce(a.cd_motivo_exc_conta::text, '') = ''
		and	(a.nr_prescricao IS NOT NULL AND a.nr_prescricao::text <> '')
		
union all

		select	a.nr_prescricao
		from	material_atend_paciente a
		where	a.nr_interno_conta = nr_interno_conta_p
		and	coalesce(a.cd_motivo_exc_conta::text, '') = ''
		and	(a.nr_prescricao IS NOT NULL AND a.nr_prescricao::text <> '')
		) alias4
	group by	nr_prescricao
	order by	nr_prescricao;


BEGIN

ds_retorno_w	:= substr(wheb_mensagem_pck.get_texto(308943),1,255) || chr(10);
nr_contador_w	:= 0;

open C01;
loop
fetch C01 into
	nr_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	nr_contador_w	:= nr_contador_w + 1;

	if (nr_contador_w = 10) then
		ds_retorno_w	:= ds_retorno_w || chr(10);
		nr_contador_w	:= 0;
	end if;

	ds_retorno_w	:= ds_retorno_w || nr_prescricao_w || ', ';

	end;
end loop;
close C01;

ds_retorno_w	:= substr(ds_retorno_w, 1, length(ds_retorno_w)-2);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
