SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_prescricoes_sem_lib_enf ( nr_atendimento_p bigint, ie_vigente_p bigint, qt_horas_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	char(1) := 'N';


BEGIN

if ((coalesce(ie_vigente_p::text, '') = '') and (coalesce(qt_horas_prescr_p::text, '') = '')) then
	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	prescr_medica where		nr_atendimento = nr_atendimento_p
	and		(dt_liberacao_medico IS NOT NULL AND dt_liberacao_medico::text <> '')
	and		coalesce(dt_suspensao::text, '') = ''
	and		coalesce(dt_liberacao::text, '') = '' LIMIT 1;
elsif (ie_vigente_p = 0) then
	ie_retorno_w := 'N';
elsif (ie_vigente_p = 1) then
	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	prescr_medica where		nr_atendimento = nr_atendimento_p
	and		(dt_liberacao_medico IS NOT NULL AND dt_liberacao_medico::text <> '')
	and		coalesce(dt_suspensao::text, '') = ''
	and		coalesce(dt_liberacao::text, '') = ''
	and     clock_timestamp() between dt_inicio_prescr and dt_validade_prescr LIMIT 1;
elsif  (ie_vigente_p = 2 AND qt_horas_prescr_p IS NOT NULL AND qt_horas_prescr_p::text <> '') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	prescr_medica where		nr_atendimento = nr_atendimento_p
	and		(dt_liberacao_medico IS NOT NULL AND dt_liberacao_medico::text <> '')
	and		coalesce(dt_suspensao::text, '') = ''
	and		coalesce(dt_liberacao::text, '') = ''
	and		((clock_timestamp()  between dt_inicio_prescr and dt_validade_prescr) or (dt_prescricao > clock_timestamp()))
	and (dt_validade_prescr	>= clock_timestamp() - qt_horas_prescr_p / 24) LIMIT 1;
end if;

return 	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
