CREATE OR REPLACE FUNCTION obter_prescritor_hor_glic (ie_tipo_item_p text, nr_seq_horario_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
cd_prescritor_w	varchar(10);
nm_prescritor_w	varchar(60);
ie_medico_w		varchar(1);
ds_informacao_w	varchar(60);

/* 
C - Código pessoa física 
N - Nome pessoa física 
M - Se médico 
*/
 
 

BEGIN 
if (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') and (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then 
	if (ie_tipo_item_p = 'P') then 
		select	substr(obter_dados_usuario_opcao(nm_usuario_dose_esp,'C'),1,10) cd_prescritor_de, 
			substr(obter_nome_pf(substr(obter_dados_usuario_opcao(nm_usuario_dose_esp,'C'),1,10)),1,60) nm_prescritor_de, 
			substr(obter_se_medico(substr(obter_dados_usuario_opcao(nm_usuario_dose_esp,'C'),1,10),'M'),1,1) ie_medico 
		into STRICT	cd_prescritor_w, 
			nm_prescritor_w, 
			ie_medico_w 
		from	prescr_proc_hor 
		where	nr_sequencia = nr_seq_horario_p 
		and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';
 
		if (ie_opcao_p = 'C') then 
			ds_informacao_w := cd_prescritor_w;
		elsif (ie_opcao_p = 'N') then 
			ds_informacao_w := nm_prescritor_w;
		elsif (ie_opcao_p = 'M') then 
			ds_informacao_w := ie_medico_w;
		end if;
	end if;
end if;
 
return ds_informacao_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
