CREATE OR REPLACE FUNCTION obter_prescr_dieta_interv ( cd_intervalo_p text, ie_item_p text) RETURNS varchar AS $body$
DECLARE


ie_prescricao_dieta_w		varchar(1);
ie_visualiza_item_w		varchar(255) := 'S';
cont_w				bigint;


BEGIN

select	count(*)
into STRICT	cont_w
from	regra_intervalo_exclusivo
where	cd_intervalo = cd_intervalo_p;

if (cont_w > 0) then

	select	count(*)
	into STRICT	cont_w
	from	regra_intervalo_exclusivo
	where	cd_intervalo 		= cd_intervalo_p
	and	ie_prescricao_dieta	= ie_item_p;

	if (cont_w = 0) then
		ie_visualiza_item_w := 'N';
	end if;
end if;

return ie_visualiza_item_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
