SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_prescr_esp_fornec_int ( nr_cirurgia_p bigint, cd_fornec_consig_p text) RETURNS bigint AS $body$
DECLARE


nr_prescricao_w		bigint;
qt_existe_w		bigint;


/*busca a prescrição da pasta materiais especiais da gestão de cirurgias passando o fornecedor processo da integração com INPART*/

BEGIN

select 	count(*)
into STRICT	qt_existe_w
from   	prescr_medica p
where  	p.nr_cirurgia = nr_cirurgia_p
and 	p.ie_tipo_prescr_cirur = '1';


if (coalesce(qt_existe_w,0) > 0) then

	select 	max(p.nr_prescricao)
	into STRICT 	nr_prescricao_w
	from   	prescr_medica p,
		prescr_material m
	where  	p.nr_cirurgia = nr_cirurgia_p
	and 	p.nr_prescricao = m.nr_prescricao
	and 	m.cd_fornec_consignado = cd_fornec_consig_p
	and 	p.ie_tipo_prescr_cirur = '1'
	and exists (	SELECT 1
			from prescr_material x
			where x.nr_prescricao = p.nr_prescricao);

end if;

return nr_prescricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
