SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_prescr_ext_lab (nr_presc_ext_p text) RETURNS bigint AS $body$
DECLARE


nr_prescricao_w		bigint;
nr_prescricao_ext_w	bigint;
cd_identificador_w	varchar(5);


BEGIN

if (nr_presc_ext_p IS NOT NULL AND nr_presc_ext_p::text <> '') then

	cd_identificador_w := upper(substr(nr_presc_ext_p,1,1));
	nr_prescricao_ext_w:= substr(nr_presc_ext_p,2,length(nr_presc_ext_p));

	select	max(nr_prescricao)
	into STRICT	nr_prescricao_w
	from	lab_tasylab_cliente c,
		lab_tasylab_cli_prescr p
	where	c.cd_identificador 		= cd_identificador_w
	and	c.nr_sequencia		= p.nr_seq_tasylab_cli
	and	p.nr_prescricao_origem	= nr_prescricao_ext_w;

end if;

return	nr_prescricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
