CREATE OR REPLACE FUNCTION obter_prescr_guia_conv (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


nr_atendimento_w	bigint;
cd_tipo_conenveio_w	smallint;
nr_doc_convenio_w	varchar(20);
ds_retorno_w				varchar(20);


BEGIN

select 	max(nr_atendimento),
	max(nr_doc_conv)
into STRICT	nr_atendimento_w,
	nr_doc_convenio_w
from	prescr_medica
where 	nr_prescricao = nr_prescricao_p;
ds_retorno_w	:='N';
if (nr_atendimento_w > 0) then

	select	OBTER_TIPO_CONVENIO(OBTER_CONVENIO_ATENDIMENTO(nr_atendimento_w))
	into STRICT	cd_tipo_conenveio_w
	;

	if (cd_tipo_conenveio_w <> 1) and (coalesce(nr_doc_convenio_w::text, '') = '') then

		ds_retorno_w	:= 'S';

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
