CREATE OR REPLACE FUNCTION obter_prescr_medica_complem ( nr_prescricao_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'N';


BEGIN

if (nr_prescricao_p > 0) then
	select IE_LIBERACAO_CPOE
	into STRICT ie_retorno_w
	from PRESCR_MEDICA_COMPL
	where NR_PRESCRICAO = nr_prescricao_p;
end if;

return	coalesce(ie_retorno_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
