CREATE OR REPLACE FUNCTION obter_prescr_nao_lib ( nr_atendimento_p bigint, cd_prescritor_p text, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE

nr_prescricao_w	bigint;

BEGIN
select	coalesce(max(nr_prescricao),0)
into STRICT		nr_prescricao_w
from		prescr_medica
where	nr_atendimento = nr_atendimento_p
and		cd_prescritor = cd_prescritor_p
and		coalesce(dt_liberacao::text, '') = ''
and		coalesce(dt_liberacao_medico::text, '') = ''
and		coalesce(dt_liberacao_farmacia::text, '') = ''
and		dt_prescricao between clock_timestamp() - interval '1 days' and clock_timestamp() + interval '1 days';

return nr_prescricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
