CREATE OR REPLACE FUNCTION obter_prescr_nao_padronizado (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_convenio_w	smallint;
cont_w			bigint;
ie_resultado_w		varchar(10) := 'N';


BEGIN

select	count(*)
into STRICT	cont_w
from	regra_pres_nao_padronizado;

if (cont_w > 0) then

	select	coalesce(ie_tipo_convenio,0)
	into STRICT	ie_tipo_convenio_w
	from	atendimento_paciente
	where	nr_atendimento = nr_atendimento_p;

	select	coalesce(max(ie_prescricao), CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END )
	into STRICT	ie_resultado_w
	from	regra_pres_nao_padronizado
	where	coalesce(ie_tipo_convenio, coalesce(ie_tipo_convenio_w,0)) = coalesce(ie_tipo_convenio_w,0);

end if;
	
return	coalesce(ie_resultado_w,'S');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
