CREATE OR REPLACE FUNCTION obter_prescr_nao_receb_setor ( cd_pessoa_fisica_p text, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := '';
nr_prescricao_w		bigint;

c01 CURSOR FOR
	SELECT	/*+ index (a presmed_i1) */		a.nr_prescricao
	from	prescr_medica a
	where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	dt_prescricao		between clock_timestamp() - interval '1 days' and clock_timestamp() + interval '2 days'
	and	((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.dt_liberacao_medico IS NOT NULL AND a.dt_liberacao_medico::text <> ''))
	and	not exists (	SELECT	1
					from	prescr_medic_setor b
					where	b.cd_setor_atendimento	= cd_setor_atendimento_p
					and	b.dt_recebimento	between clock_timestamp() - interval '1 days' and clock_timestamp()
					and	a.nr_prescricao	= b.nr_prescricao)
	order by a.nr_prescricao;


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	nr_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ds_retorno_w	:= ds_retorno_w ||to_char(nr_prescricao_w) ||' - ';
	if (length(ds_retorno_w) > 220) then
		exit;
	end if;
END LOOP;
CLOSE C01;


return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
