SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_prescr_nutricao ( nr_atendimento_p bigint, ie_tipo_dieta_p bigint) RETURNS bigint AS $body$
DECLARE


nr_prescricao_w		bigint;
prescr_delegar_w	varchar(10);


BEGIN

prescr_delegar_w := obter_param_usuario(1000, 18, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, prescr_delegar_w);

if (ie_tipo_dieta_p = 0) then	/* Todas */
	select	max(a.nr_prescricao)
	into STRICT	nr_prescricao_w
	from	prescr_nutricao b,
		prescr_medica a
	where	a.nr_prescricao		= b.nr_prescricao
	and	a.nr_atendimento	= nr_atendimento_p
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	AND	(((prescr_delegar_w = 'S') AND ((b.ie_delegacao_total = 'S') OR (ie_dieta_oral = 'D') OR (ie_suplemento = 'D') OR (ie_enteral = 'D') OR (ie_npt = 'D'))) OR (prescr_delegar_w = 'N'));

elsif (ie_tipo_dieta_p = 1) then	/* Oral */
	select	max(a.nr_prescricao)
	into STRICT	nr_prescricao_w
	from	prescr_nutricao b,
		prescr_medica a
	where	a.nr_prescricao		= b.nr_prescricao
	and	a.nr_atendimento	= nr_atendimento_p
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and	((b.ie_delegacao_total = 'S') or (ie_dieta_oral = 'D'));

elsif (ie_tipo_dieta_p = 2) then	/* Suplemento */
	select	max(a.nr_prescricao)
	into STRICT	nr_prescricao_w
	from	prescr_nutricao b,
		prescr_medica a
	where	a.nr_prescricao		= b.nr_prescricao
	and	a.nr_atendimento	= nr_atendimento_p
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and	((b.ie_delegacao_total = 'S') or (ie_suplemento = 'D'));

elsif (ie_tipo_dieta_p = 3) then	/* Enteral */
	select	max(a.nr_prescricao)
	into STRICT	nr_prescricao_w
	from	prescr_nutricao b,
		prescr_medica a
	where	a.nr_prescricao		= b.nr_prescricao
	and	a.nr_atendimento	= nr_atendimento_p
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and	((b.ie_delegacao_total = 'S') or (ie_enteral = 'D'));

elsif (ie_tipo_dieta_p = 4) then	/* NPT */
	select	max(a.nr_prescricao)
	into STRICT	nr_prescricao_w
	from	prescr_nutricao b,
		prescr_medica a
	where	a.nr_prescricao		= b.nr_prescricao
	and	a.nr_atendimento	= nr_atendimento_p
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and	((b.ie_delegacao_total = 'S') or (ie_npt = 'D'));
end if;

return	nr_prescricao_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
