CREATE OR REPLACE FUNCTION obter_prescr_pac_data_valida ( cd_pessoa_fisica_p text, dt_data_p timestamp, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


qt_prescr_w		bigint;
ds_retorno_w		varchar(2) := 'N';


BEGIN

select 	count(*)
into STRICT	qt_prescr_w
from 	prescr_medica
where 	cd_pessoa_fisica 	= cd_pessoa_fisica_p
and	nr_prescricao		= nr_prescricao_p
and 	dt_data_p 		between dt_inicio_prescr and dt_validade_prescr;

if (qt_prescr_w > 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
