CREATE OR REPLACE FUNCTION obter_prescr_proc_ciente (nr_prescricao_p bigint, nr_sequencia_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


nm_usuario_w		varchar(15);
ds_retorno_w		varchar(20);
dt_atualizacao_w	timestamp;


BEGIN

select	coalesce(max(nm_usuario),''),
	coalesce(max(dt_atualizacao),clock_timestamp())
into STRICT	nm_usuario_w,
	dt_atualizacao_w
from	prescr_proc_ciente
where	nr_prescricao	= nr_prescricao_p
  and	nr_sequencia	= nr_sequencia_p;

if (ie_opcao_p = 1) then
	ds_retorno_w := nm_usuario_w;
elsif (ie_opcao_p = 2) then
	ds_retorno_w := to_char(dt_atualizacao_w, 'dd/mm/yyyy hh24:mi:ss');
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
