CREATE OR REPLACE FUNCTION obter_prescr_solic_bco_sangue (nr_prescricao_p bigint, ie_opcao_p text, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

/*
Atualmente apenas nr_prescricao_p é utilizado, posteriormente, poderá ser util o uso da sequencia da PRESCR_SOLIC_BCO_SANGUE em outras ocasições

IE_OPCAO_P

HOA	-> QT_HEMOGLOBINA
HTC	-> QT_HEMATOCRITO
PLQ	-> QT_PLAQUETA
DIAG	-> DS_DIAGNOSTICO
r	- RESERVADO
*/
ds_retorno_w	varchar(300) := null;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	if (ie_opcao_p = 'HOA') then
		select	max(qt_hemoglobina)
		into STRICT	ds_retorno_w
		from	prescr_solic_bco_sangue
		where	nr_prescricao	= nr_prescricao_p;

	elsif (ie_opcao_p = 'HTC') then
		select	max(qt_hematocrito)
		into STRICT	ds_retorno_w
		from	prescr_solic_bco_sangue
		where	nr_prescricao = nr_prescricao_p;

	elsif (ie_opcao_p = 'R') then
		select	max(ie_reserva)
		into STRICT	ds_retorno_w
		from	prescr_solic_bco_sangue
		where	nr_prescricao	= nr_prescricao_p;

	elsif (ie_opcao_p = 'PLQ') then
		select	max(qt_plaqueta)
		into STRICT	ds_retorno_w
		from	prescr_solic_bco_sangue
		where	nr_prescricao	= nr_prescricao_p;

	elsif (ie_opcao_p = 'DIAG') then
		select 	max(ds_diagnostico)
		into STRICT	ds_retorno_w
		from 	prescr_solic_bco_sangue
		where 	nr_prescricao = nr_prescricao_p;

	elsif (ie_opcao_p = 'TIPOA') then
		select	max(substr(obter_valor_dominio(1223,ie_tipo),1,255))
		into STRICT	ds_retorno_w
		from	prescr_solic_bco_sangue
		where	nr_prescricao	= nr_prescricao_p;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
