CREATE OR REPLACE FUNCTION obter_presc_ultimo_sae_date (nr_atendimento_p text, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE

			 
/* opção				*/
 
/* 	DI = data inicio sae 		*/
 
/*	DF = data fim sae 		*/
 
/*	DP = data prescrição*/
 
/*	DT= Data Liberação*/
 
 
dt_inicio_w			timestamp;
dt_fim_w			timestamp;
dt_prescricao_w		timestamp;
dt_liberacao_w   timestamp;
ds_retorno_w		timestamp;
nr_sequencia_w		bigint;


BEGIN 
 
SELECT MAX(b.nr_sequencia) 
into STRICT	nr_sequencia_w 
FROM 	pe_prescricao b 
where	b.nr_atendimento = nr_atendimento_p 
and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '') 
and	coalesce(b.dt_inativacao::text, '') = '';
 
if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then 
	SELECT	DT_INICIO_PRESCR, 
			DT_VALIDADE_PRESCR, 
			dt_prescricao, 
			dt_liberacao 
	into STRICT	dt_inicio_w, 
			dt_fim_w, 
			dt_prescricao_w, 
			dt_liberacao_w 
	FROM	pe_prescricao 
	WHERE	nr_sequencia = nr_sequencia_w;
 
	if (ie_opcao_p = 'DI') then 
		ds_retorno_w := dt_inicio_w;
	elsif (ie_opcao_p = 'DF') then 
		ds_retorno_w := dt_fim_W;
	elsif (ie_opcao_p = 'DP') then 
		ds_retorno_w := dt_prescricao_w;
	elsif (ie_opcao_p = 'DT') then 
		ds_retorno_w := dt_liberacao_w;
	end if;
end if;
return	ds_retorno_w;
 
 
end;
$body$
