CREATE OR REPLACE FUNCTION obter_prestador_convenio (cd_cgc_p text, cd_convenio_p bigint, cd_categoria_p text default null, nr_seq_classificacao_p bigint default null) RETURNS varchar AS $body$
DECLARE


cd_prestador_convenio_w		varchar(30);
cd_categoria_w			varchar(10);
nr_seq_classificacao_w		bigint;

C01 CURSOR FOR
	SELECT	cd_prestador_convenio
	from	convenio_prestador
	where	cd_convenio		= cd_convenio_p
	and	cd_cgc			= cd_cgc_p
	and	coalesce(cd_categoria,cd_categoria_w) = cd_categoria_w
	and	coalesce(nr_seq_classificacao,nr_seq_classificacao_w) = nr_seq_classificacao_w
	order by coalesce(cd_categoria,'0'),
		 coalesce(nr_seq_classificacao,0);


BEGIN
cd_prestador_convenio_w	:= '';
cd_categoria_w		:= coalesce(cd_categoria_p,'0');
nr_seq_classificacao_w	:= coalesce(nr_seq_classificacao_p,0);

if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		cd_prestador_convenio_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
end if;

RETURN cd_prestador_convenio_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
