CREATE OR REPLACE FUNCTION obter_prestador_material ( cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, ie_tipo_atendimento_p bigint, cd_setor_atendimento_p bigint, cd_grupo_material_p bigint, cd_subgrupo_material_p bigint, cd_classe_material_p bigint, cd_material_p bigint, cd_medico_resp_p text, dt_atendimento_p timestamp, cd_cgc_fornecedor_p text) RETURNS varchar AS $body$
DECLARE


cd_cgc_prestador_w			varchar(14);
cd_perfil_w				integer;
dt_atendimento_w			timestamp;

C01 CURSOR FOR
	SELECT cd_cgc_prestador
	from	define_prestador_material
	where	cd_estabelecimento					= cd_estabelecimento_p
	and	coalesce(cd_convenio,cd_convenio_p)			= cd_convenio_p
	and	coalesce(cd_categoria,cd_categoria_p)			= cd_categoria_p
	and	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_p)		= ie_tipo_atendimento_p
	and	coalesce(cd_setor_atendimento,cd_setor_atendimento_p)	= cd_setor_atendimento_p
	and	coalesce(cd_grupo_material,cd_grupo_material_p)		= cd_grupo_material_p
	and	coalesce(cd_subgrupo_material,cd_subgrupo_material_p)	= cd_subgrupo_material_p
	and	coalesce(cd_classe_material,cd_classe_material_p)		= cd_classe_material_p
	and	coalesce(cd_material,cd_material_p)				= cd_material_p
	and	coalesce(cd_perfil,cd_perfil_w)				= cd_perfil_w
	and	coalesce(cd_medico_resp,coalesce(cd_medico_resp_p,'0'))		= coalesce(cd_medico_resp_p,'0')
	and 	coalesce(cd_cgc_fornecedor, coalesce(cd_cgc_fornecedor_p, '0'))   = coalesce(cd_cgc_fornecedor_p, '0')
	and	dt_atendimento_w between coalesce(dt_inicio_vigencia, dt_atendimento_w) and fim_dia(coalesce(dt_final_vigencia, dt_atendimento_w))
	order by
		coalesce(cd_material,0),
		coalesce(cd_classe_material,0),
		coalesce(cd_subgrupo_material,0),
		coalesce(cd_grupo_material,0),
		coalesce(cd_setor_atendimento,0),
		coalesce(ie_tipo_atendimento,0),
		coalesce(cd_medico_resp,'0'),
		coalesce(cd_perfil,0),
		coalesce(cd_categoria,' '),
		coalesce(cd_convenio,0),
		coalesce(cd_cgc_fornecedor, '0');


BEGIN

cd_cgc_prestador_w	:= ' ';
cd_perfil_w		:= Obter_Perfil_Ativo;
dt_atendimento_w	:= coalesce(dt_atendimento_p, clock_timestamp());

OPEN C01;
LOOP
FETCH C01 into
	cd_cgc_prestador_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	cd_cgc_prestador_w := cd_cgc_prestador_w;
	end;
END LOOP;
CLOSE C01;

if (cd_cgc_prestador_w = ' ') then
	begin
	select	max(cd_cgc)
	into STRICT	cd_cgc_prestador_w
	from	estabelecimento
	where	cd_estabelecimento	 = cd_estabelecimento_p;
	end;
end if;

RETURN cd_cgc_prestador_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
