CREATE OR REPLACE FUNCTION obter_previsao_alta_vencimento ( nm_paciente_p text, dt_previsao_p timestamp, dt_atual_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000);
ds_hora_w		varchar(2);
ds_min_w		varchar(2);
dt_alta_prevista_w varchar(15);


BEGIN

if (dt_previsao_p IS NOT NULL AND dt_previsao_p::text <> '') and (dt_atual_p IS NOT NULL AND dt_atual_p::text <> '')  then
	begin

	dt_alta_prevista_w := coalesce(obter_dif_data(dt_atual_p, dt_previsao_p, null),'');
	ds_hora_w	:= coalesce(substr(dt_alta_prevista_w,1,2), '');
	ds_min_w :=		coalesce(substr(dt_alta_prevista_w,4,2), '');

	ds_retorno_w := coalesce(obter_texto_dic_objeto(420827, wheb_usuario_pck.get_nr_seq_idioma, 'PACIENTE=' || nm_paciente_p || ';HORAS=' || ds_hora_w || ';MINUTOS=' || ds_min_w),'');

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
