SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_prev_cron_proj ( nr_seq_crono_p bigint, dt_ref_p timestamp, ie_apenas_cron_aprov_p text default 'N') RETURNS bigint AS $body$
DECLARE


pr_retorno_w		double precision := 0;
hrs_prev_w		double precision;
dias_uteis_w		double precision;
dias_uteis_data_atual_w	double precision;
hrs_executadas_prev_w	double precision;
total_hrs_prev_w	double precision := 0;
total_hrs_exec_prev_w   double precision := 0;
dt_inicio_prev_w	timestamp;

C01 CURSOR FOR

SELECT  e.qt_hora_prev hrs_previstas,
	obter_dias_uteis_periodo(e.dt_inicio_prev, e.dt_fim_prev, 1) dias_uteis,
        obter_dias_uteis_periodo(e.dt_inicio_prev, trunc(dt_ref_p), 1) dias_uteis_data_atual
from    proj_projeto p,
	proj_cronograma c,
	proj_cron_etapa e
where	p.nr_sequencia = c.nr_seq_proj
and     c.nr_sequencia = e.nr_seq_cronograma
and	((ie_apenas_cron_aprov_p =  'N') or (ie_apenas_cron_aprov_p = 'S' and (c.dt_aprovacao IS NOT NULL AND c.dt_aprovacao::text <> '')))
and     c.ie_situacao  = 'A'
and	c.nr_sequencia =  nr_seq_crono_p
and	exists (
			SELECT	1
			from	proj_cron_etapa xx
			where	coalesce(xx.nr_seq_superior::text, '') = ''
			and	e.nr_sequencia = xx.nr_sequencia
		)
order by c.ie_tipo_cronograma, e.dt_inicio_prev;


BEGIN

	select	dt_inicio
	into STRICT	dt_inicio_prev_w
	from	PROJ_CRONOGRAMA 
	where	nr_sequencia = nr_seq_crono_p;

	if (dt_ref_p < dt_inicio_prev_w) then
		pr_retorno_w := 0;

	else

		select 	coalesce(max(a.pr_previsto), 0) pr_previsto
		into STRICT 	pr_retorno_w
		from 	proj_cron_etapa a
		where 	a.nr_seq_cronograma = nr_seq_crono_p
		and 	coalesce(a.nr_seq_superior::text, '') = ''
		and (SELECT count(b.nr_sequencia)
				from proj_cron_etapa b
				where b.nr_seq_cronograma = nr_seq_crono_p
				and coalesce(b.nr_seq_superior::text, '') = '') = 1;

		if (pr_retorno_w > 0) then
			return  pr_retorno_w;
		end if;

		open C01;
		loop
		fetch C01 into
			hrs_prev_w,
			dias_uteis_w,
			dias_uteis_data_atual_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

				if (dias_uteis_data_atual_w >= dias_uteis_w) then
					hrs_executadas_prev_w := hrs_prev_w;
				else
					hrs_executadas_prev_w := ((dias_uteis_data_atual_w / dias_uteis_w) * hrs_prev_w);
				end if;

				total_hrs_exec_prev_w := total_hrs_exec_prev_w + hrs_executadas_prev_w;
				total_hrs_prev_w := total_hrs_prev_w + hrs_prev_w;

			end;
		end loop;
		close C01;

		if (total_hrs_exec_prev_w = 0 and total_hrs_prev_w = 0)then
			pr_retorno_w := 0;
		else
			pr_retorno_w := ((total_hrs_exec_prev_w / total_hrs_prev_w) * 100);
		end if;
	end if;

return  pr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
