CREATE OR REPLACE FUNCTION obter_primeira_dialise (cd_pessoa_fisica_p bigint) RETURNS varchar AS $body$
DECLARE

    data_inicio_w varchar(255);

BEGIN

    SELECT to_char(MIN(dt_inicio_tratamento), 'dd/mm/yyyy')
      INTO STRICT data_inicio_w
      FROM hd_pac_renal_cronico
     WHERE cd_pessoa_fisica = cd_pessoa_fisica_p;

    RETURN data_inicio_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
