SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_primeira_letra_mx ( ds_texto_p text, ie_posicao_p text, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
	ie_posicao_p
		I - inicial
		N - interna

	ie_tipo_p
		C - consoante
		V - vogal
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w			varchar(255)	:= null;
ds_texto_w			varchar(255);
ds_letra_w			varchar(255);


BEGIN
ds_texto_w	:= substr(ds_texto_p,1,255);

if (ie_posicao_p = 'N') then
	ds_texto_w	:= substr(ds_texto_p,2,length(ds_texto_p)-1);
end if;

while(length(ds_texto_w) > 0) loop
	begin
	ds_letra_w	:= substr(ds_texto_w,1,1);

	if (ie_tipo_p = 'C') and (ds_letra_w not in ('A','E','I','O','U')) then
		ds_retorno_w	:= ds_letra_w;
		exit;
	elsif (ie_tipo_p = 'V') and (ds_letra_w in ('A','E','I','O','U')) then
		ds_retorno_w	:= ds_letra_w;
		exit;
	end if;

	ds_texto_w	:= substr(ds_texto_w,2,255);
	end;
end loop;

if (ie_tipo_p = 'C') and (coalesce(ds_retorno_w::text, '') = '') then
	ds_retorno_w	:= 'X';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
