CREATE OR REPLACE FUNCTION obter_primeira_prescricao ( nr_seq_pend_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


nr_prescricao_w		paciente_atendimento.nr_prescricao%type;

BEGIN

select	coalesce(max(nr_prescricao), '')
into STRICT	nr_prescricao_w
from	paciente_atendimento
where	nr_seq_pend_agenda 	= nr_seq_pend_agenda_p
and		nr_ciclo = 1
and		ds_dia_ciclo = 'D1';

return	nr_prescricao_w;

end;
